mapper.select:
select 

	from td_shipper 
	where td_shipper.id in (
		select distinct id from td_shipper inner join

		(select 
			distinct id
			from td_shipper 

	        and td_shipper.id = #{entity.id}
	        and td_shipper.customer_id = #{entity.customerId}
	        and td_shipper.code = #{entity.code}
	        and td_shipper.reg_address = #{entity.regAddress}
	        and td_shipper.capital = #{entity.capital}
	        and td_shipper.level = #{entity.level}
	        and td_shipper.has_permit = #{entity.hasPermit}
	        and td_shipper.name = #{entity.name}
	        and td_shipper.short_name = #{entity.shortName}
	        and td_shipper.tax_no = #{entity.taxNo}
	        and td_shipper.business_type = #{entity.businessType}
	        and td_shipper.car_number = #{entity.carNumber}
	        and td_shipper.linkman = #{entity.linkman}
	        and td_shipper.owner = #{entity.owner}
	        and td_shipper.phone = #{entity.phone}
	        and td_shipper.tel = #{entity.tel}
	        and td_shipper.cert_status = #{entity.certStatus}
	        and td_shipper.cert_valid_time = #{entity.certValidTime}
	        and td_shipper.has_inovice = #{entity.hasInovice}
	        and td_shipper.desc = #{entity.desc}
	        and td_shipper.create_user = #{entity.createUser}
	        and td_shipper.create_time = #{entity.createTime}
	        and td_shipper.update_user = #{entity.updateUser}
	        and td_shipper.update_time = #{entity.updateTime}

		) ${entity.tableAlias} using (id)

)

