mapper.select:
select 

	from tps_crm_complain_charge 
	where tps_crm_complain_charge.lineid in (
		select distinct lineid from tps_crm_complain_charge inner join

		(select 
			distinct lineid
			from tps_crm_complain_charge 

	        and tps_crm_complain_charge.lineid = #{entity.lineid}
	        and tps_crm_complain_charge.complain_charge_lineid = #{entity.complainChargeLineid}
	        and tps_crm_complain_charge.complain_id = #{entity.complainId}
	        and tps_crm_complain_charge.customer_order = #{entity.customerOrder}
	        and tps_crm_complain_charge.category = #{entity.category}
	        and tps_crm_complain_charge.category_id = #{entity.categoryId}
	        and tps_crm_complain_charge.category_name = #{entity.categoryName}
	        and tps_crm_complain_charge.charger_type = #{entity.chargerType}
	        and tps_crm_complain_charge.charger_id = #{entity.chargerId}
	        and tps_crm_complain_charge.charger_name = #{entity.chargerName}
	        and tps_crm_complain_charge.percent = #{entity.percent}
	        and tps_crm_complain_charge.charge_memo = #{entity.chargeMemo}
	        and tps_crm_complain_charge.charge_time = #{entity.chargeTime}
	        and tps_crm_complain_charge.create_user = #{entity.createUser}
	        and tps_crm_complain_charge.createtime = #{entity.createtime}
	        and tps_crm_complain_charge.realate_flag = #{entity.realateFlag}
	        and tps_crm_complain_charge.realate_lineid = #{entity.realateLineid}
	        and tps_crm_complain_charge.create_username = #{entity.createUsername}
	        and tps_crm_complain_charge.create_time = #{entity.createTime}
	        and tps_crm_complain_charge.active = #{entity.active}
	        and tps_crm_complain_charge.active_user = #{entity.activeUser}
	        and tps_crm_complain_charge.active_time = #{entity.activeTime}
	        and tps_crm_complain_charge.active_memo = #{entity.activeMemo}
	        and tps_crm_complain_charge.complain_type_relate_flag = #{entity.complainTypeRelateFlag}
	        and tps_crm_complain_charge.complain_type_realate_lineid = #{entity.complainTypeRealateLineid}

		) ${entity.tableAlias} using (lineid)

)
