mapper.select:
select 

	from tps_quotation_audit_set 
	where tps_quotation_audit_set.lineid in (
		select distinct lineid from tps_quotation_audit_set inner join

		(select 
			distinct lineid
			from tps_quotation_audit_set 

	        and tps_quotation_audit_set.lineid = #{entity.lineid}
	        and tps_quotation_audit_set.type = #{entity.type}
	        and tps_quotation_audit_set.flowid = #{entity.flowid}
	        and tps_quotation_audit_set.overflow_down = #{entity.overflowDown}
	        and tps_quotation_audit_set.overflow_up = #{entity.overflowUp}
	        and tps_quotation_audit_set.audit_user = #{entity.auditUser}
	        and tps_quotation_audit_set.assignuser = #{entity.assignuser}
	        and tps_quotation_audit_set.audit_rule = #{entity.auditRule}
	        and tps_quotation_audit_set.create_user = #{entity.createUser}
	        and tps_quotation_audit_set.create_time = #{entity.createTime}
	        and tps_quotation_audit_set.active = #{entity.active}
	        and tps_quotation_audit_set.active_user = #{entity.activeUser}
	        and tps_quotation_audit_set.active_time = #{entity.activeTime}
	        and tps_quotation_audit_set.active_memo = #{entity.activeMemo}

		) ${entity.tableAlias} using (lineid)

)

